import { useSelector } from 'react-redux';
import React, { useEffect, useState } from 'react';
import { Rootstate } from '../../store/store';

interface IModal {
	closeButton: Function;
}

const AddRest: React.FC<IModal> = (props: IModal) => {
	const dishesData = useSelector((state: Rootstate) => state.dishes.value);
	const [comment, setComment] = useState<string[]>(['']);
	const [informationDIsh, setinformationDIsh] = useState<string[]>(['']);
	const [quantity, setQuantity] = useState<number>(0);
	const [number, setNumber] = useState(0);
	const handlePlus = () => {
		setNumber(number + 1);
	};

	const handleMinus = () => {
		setNumber(number - 1);
	};

	const postInformation = () => {
		return comment;
		//to come back!!
	};

	return (
		<div className="restaurants-card">
			<div
				id="restaurants-card-Modal"
				className="modal">
				<div className="content">
					<form onSubmit={postInformation}>
						<span
							id="closeButton"
							onClick={() => props.closeButton()}
							className="close">
							&times;
						</span>
						<div className="rest-information">
							<div id="choose-a-side">
								<button
									value={number}
									id=""
									type="submit"
									onChange={(e: any) => setNumber(e.target.value)}>
									ADD RESTAURANT
								</button>
							</div>
						</div>
					</form>
				</div>
			</div>
		</div>
	);
};

export default AddRest;
