import { createSlice } from '@reduxjs/toolkit';
import { IRestaurants } from '../../components/RestaurantPage/RestaurantPage';
// import data from '../../data.json';

const data1 = () => {
	fetch('http://localhost:8000/restaurant/', {
		method: 'GET',
	})
		.then((response) => response.json())
		.then((data) => {
			return data;
		})
		.catch((err) => {
			console.log(err.message);
		});
};

const newData = await data1();
export const restaurantsSlice = createSlice({
	name: 'restaurants',
	initialState: {
		value: newData,
		filteredValue: newData,
		// filteredValue: data.restaurant,
	},
	reducers: {
		setAllRestuarants: (state) => {
			state.filteredValue = data1();
		},
		// setPopularRestuarants: (state) => {
		// 	const newData = await data1();
		// 	state.filteredValue = [...newData].sort(
		// 		(a: any, b: any) => b.views - a.views
		// 	);
		// },
		// setNewRestuarants: (state) => {
		// 	state.filteredValue = newData.filter(
		// 		(restaurant) => restaurant.newRest === true
		// 	);
		// },
		setOpenNow: (state, action) => {
			state.filteredValue = action.payload;
		},
		setMap: (state) => {
			state.filteredValue = data1();
		},
	},
});

export const {
	setAllRestuarants,
	setOpenNow,
	setMap,
	// setPopularRestuarants,
	// setNewRestuarants,
} = restaurantsSlice.actions;

export default restaurantsSlice.reducer;
