import React, { useState } from 'react';
import { useSelector } from 'react-redux';
import { Rootstate } from '../../store/store';
import { IRestaurants } from '../RestaurantPage/RestaurantPage';

interface IModal {
	closeButton: Function;
}

const AddRest: React.FC<IModal> = (props: IModal) => {
	const [comment, setComment] = useState<string[]>(['']);
	interface InputField {
		id: string;
		placeholder: string;
		type: string;
		title?: string;
		name: string;
	}
	const restDetails: InputField[] = [
		{
			id: 'name-input',
			placeholder: 'Enter restaurant name',
			type: 'text',
			title: 'Restaurant Name',
			name: 'rest-name',
		},
		{
			id: 'chef-input',
			placeholder: 'Enter chef name',
			type: 'text',
			title: 'Chef Name',
			name: 'chef-name',
		},
		{
			id: 'open-hours-input',
			placeholder: 'Enter open hours',
			type: 'text',
			title: 'Open Hours',
			name: 'open-hours',
		},
		{
			id: 'address-input',
			placeholder: 'Enter restaurant address',
			type: 'text',
			title: 'Address',
			name: 'Address',
		},
		{
			id: 'open-days-input',
			placeholder: 'Enter open days',
			type: 'text',
			title: 'Open Days',
			name: 'open-days',
		},
		{
			id: 'open-year-input',
			placeholder: 'Enter open year',
			type: 'text',
			title: 'Open Year',
			name: 'open-years',
		},
		{
			id: 'img-input',
			placeholder: 'Enter image URL',
			type: 'text',
			title: 'Image',
			name: 'img',
		},
		{
			id: 'dishes-input',
			placeholder: 'Enter dishes',
			type: 'text',
			title: 'Dishes',
			name: 'dishes',
		},
		{
			id: 'rating-input',
			placeholder: 'Enter rating',
			type: 'text',
			title: 'Rating',
			name: 'rating',
		},
	];

	const renderInputs = (inputFields: InputField[]) => {
		return inputFields.map((field) => (
			<div
				id="input-container"
				key={field.id}>
				{field.title && <div id="input-title">{field.title}</div>}
				<input
					id="input-full-Name"
					placeholder={field.placeholder}
					type={field.type}
				/>
			</div>
		));
	};

	const postInformation = () => {
		return comment;
		//to come back!!
	};

	const handSaveRest = async (e: any) => {
		e.preventDefault();
		const credentials: any = {
			id: 0,
			name: '',
			address: [],
			chef: '',
			chefId: 0,
			openHours: [],
			openDays: [],
			openYear: 0,
			img: '',
			dishes: [],
			rating: 0,
			popular: '',
			newRest: '',
			openNow: '',
		};
		const inputObj = e.target;

		Object.values(inputObj).forEach((obj: any) => {
			switch (obj.name) {
				case 'rating':
					credentials[obj.name] = Number(obj.value);
					break;
				case 'rest-name':
				case 'chef-name':
				case 'img':
				case 'Address':
					// credentials[obj.name] = String(obj.value);
					// break;
					credentials[obj.name] = obj.value.split(',').map(String);
					break;
				case 'dishes':
				case 'open-years':
				case 'open-days':
				case 'open-hours':
					credentials[obj.name] = obj.value.split(',').map(Number);
					break;
				default:
					credentials[obj.name] = obj.value;
					break;
			}
		});
	};

	return (
		<div className="restaurants-card">
			<div
				id="restaurants-card-Modal"
				className="modal">
				<div className="content">
					<form onSubmit={handSaveRest}>
						<span
							id="closeButton"
							onClick={() => props.closeButton()}
							className="close">
							&times;
						</span>
						<div className="rest-information">
							<div>
								<div id="details-information">{renderInputs(restDetails)}</div>
							</div>
							<button
								id="add-rest-butoon"
								type="submit">
								ADD RESTAURANT
							</button>
						</div>
					</form>
				</div>
			</div>
		</div>
	);
};

export default AddRest;
