import { createSlice } from '@reduxjs/toolkit';
import {
	IRestaurants,
	IrestaurantsState,
} from '../../components/RestaurantPage/RestaurantPage';
const data1 = () => {
	return fetch('http://localhost:8000/restaurant/', {
		method: 'GET',
	})
		.then((response) => response.json())
		.catch((err) => {
			console.log(err.message);
			throw new Error('Unable to fetch data from server');
		});
};
export const restaurantsSlice = createSlice({
	name: 'restaurants',
	initialState: {
		value: [] as IRestaurants[],
		filteredValue: [] as IRestaurants[],
	},
	reducers: {
		setAllRestuarants: (state) => {
			state.filteredValue = state.value;
		},
		setPopularRestuarants: (state) => {
			state.filteredValue = [...state.value].sort(
				(a: any, b: any) => b.views - a.views
			);
		},
		setNewRestuarants: (state) => {
			state.filteredValue = state.value.filter(
				(restaurant: any) => restaurant.newRest === true
			);
		},
		setOpenNow: (state, action) => {
			state.filteredValue = action.payload;
		},
		setMap: (state) => {
			state.filteredValue = state.value;
		},
	},
});
export const initializeRestaurants = () => async (dispatch: any) => {
	try {
		const data = await data1();
		dispatch(setAllRestuarants(data));
		dispatch(setPopularRestuarants());
		dispatch(setNewRestuarants());
		dispatch(setOpenNow(data));
		dispatch(setMap());
	} catch (error) {
		console.log(error.message);
	}
};

export const {
	setAllRestuarants,
	setOpenNow,
	setMap,
	setPopularRestuarants,
	setNewRestuarants,
} = restaurantsSlice.actions;

export default restaurantsSlice.reducer;
