import { useSelector } from 'react-redux';
import React, { useEffect, useState } from 'react';
import { Rootstate } from '../../store/store';

interface IModal {
	closeButton: Function;
}

const AddRest: React.FC<IModal> = (props: IModal) => {
	const dishesData = useSelector((state: Rootstate) => state.dishes.value);
	const [comment, setComment] = useState<string[]>(['']);
	const [informationDIsh, setinformationDIsh] = useState<string[]>(['']);
	const [quantity, setQuantity] = useState<number>(0);
	const [number, setNumber] = useState(0);
	interface InputField {
		id: string;
		placeholder: string;
		type: string;
		title?: string;
	}
	const paymentDetails: InputField[] = [
		{
			id: 'input-card-number',
			placeholder: '',
			type: 'text',
			title: 'Card Number',
		},
		{
			id: 'input-name-on-card',
			placeholder: '',
			type: 'text',
			title: 'Name On Card',
		},
		{ id: 'input-cvv', placeholder: '', type: 'text', title: 'CVV' },
		{
			id: 'input-expiry-date',
			placeholder: '',
			type: 'text',
			title: 'Expiry Date',
		},
	];
	const postInformation = () => {
		return comment;
		//to come back!!
	};

	return (
		<div className="restaurants-card">
			<div
				id="restaurants-card-Modal"
				className="modal">
				<div className="content">
					<form onSubmit={postInformation}>
						<span
							id="closeButton"
							onClick={() => props.closeButton()}
							className="close">
							&times;
						</span>
						<div className="rest-information">
							<div>
								<input placeholder="Rest Name"></input>
							</div>
							<button
								value={number}
								id="add-rest-butoon"
								type="submit"
								onChange={(e: any) => setNumber(e.target.value)}>
								ADD RESTAURANT
							</button>
						</div>
					</form>
				</div>
			</div>
		</div>
	);
};

export default AddRest;

