import { createSlice } from '@reduxjs/toolkit';
import {
	IRestaurants,
	IrestaurantsState,
} from '../../components/RestaurantPage/RestaurantPage';

const data1 = async (): Promise<IRestaurants[]> => {
	try {
		const response = await fetch('http://localhost:8000/restaurant/', {
			method: 'GET',
		});
		const data = await response.json();
		console.log(data);
		return data;
	} catch (err) {
		console.log();
		return [];
	}
};

const getRestaurants = async () => {
	const rest: IRestaurants[] = await data1();
	console.log(rest);
	// Do something with the restaurants
};

getRestaurants();

