import { useEffect, useState } from 'react';
import { useSelector } from 'react-redux';
import { useNavigate } from 'react-router-dom';
import { IUser } from '../../store/slices/usersSlice';
import { Rootstate } from '../../store/store';
import './SignUp.css';

const SignUp: React.FC = () => {
	const usersData = useSelector((state: Rootstate) => state.users.value);
	const [users, setUsers] = useState<any>(usersData);
	const [firstName, setFirstName] = useState<string>('');
	const [lastName, setLastName] = useState<string>('');
	const [email, setEmail] = useState<string>('');
	const [password, setPassword] = useState<string>('');
	const [connect, setconnect] = useState<boolean>(false);
	console.log(usersData[1]);
	const registerUser = async (
		firstName: string,
		lastName: string,
		email: string,
		password: string,
		connect: boolean
	) => {
		console.log(firstName, lastName, email, password);
		await fetch('http://localhost:8000/users/create', {
			method: 'POST',
			body: JSON.stringify({
				firstName: firstName,
				lastName: lastName,
				email: email,
				password: password,
				connect: connect,
			}),
			headers: {
				'Content-type': 'application/json; charset=UTF-8',
			},
		})
			.then((response) => response.json())
			.then((data) => {
				setUsers((newUsers: IUser[]) => [...newUsers, data]);
				setFirstName('');
				setLastName('');
				setEmail('');
				setPassword('');
				setconnect(true);
			})
			.catch((err) => {
				console.log(err.message);
			});
	};

	const handleRegister = (e: any) => {
		e.preventDefault();
		registerUser(firstName, lastName, email, password, connect);
	};
	const navigate = useNavigate();
	useEffect(() => {
		if (connect) {
			navigate('/');
		}
	}, [connect, navigate]);

	useEffect(() => {
		if (location.pathname === '/') {
			alert('Welcome to Epicure!');
		}
	}, []);
	return (
		<div id="sign-Up-container">
			<div id="sign-up-title">
				<div id="sign-up">SIGN UP</div>
			</div>
			<div id="sign-up-information">
				<form onSubmit={handleRegister}>
					<input
						value={firstName}
						onChange={(e) => setFirstName(e.target.value)}
						id="input-sign-up-information"
						placeholder="First Name"
						type={'text'}
					/>
					<input
						value={lastName}
						onChange={(e) => setLastName(e.target.value)}
						id="input-sign-up-information"
						placeholder="Last Name"
						type={'text'}
					/>
					<input
						value={email}
						onChange={(e) => setEmail(e.target.value)}
						id="input-sign-up-information"
						placeholder="Email"
						type={'text'}
					/>
					<input
						value={password}
						onChange={(e) => setPassword(e.target.value)}
						id="input-sign-up-information"
						placeholder="Password"
						type={'password'}
					/>{' '}
					<button
						// onClick={() => navigate('/')}
						id="submit-button">
						SUBMIT
					</button>
				</form>
			</div>
		</div>
	);
};
export default SignUp;
